import React, { useCallback, useState } from 'react'
import Form from './Form'
import UploadImageZone from './UploadImageZone'

const FormHasImage = () => {
    const [formData, setFormData] = useState({
        image: ''
    })

    const handleImageChange = useCallback((value) => {
        setFormData({ ...formData, image: value });
    }, [formData])

    return (
        <Form title='Prompt Sketch to Image' onChange={(data) => setFormData({ ...formData, ...data })}>
            <UploadImageZone fileBase64={formData.image} onChange={handleImageChange} />
        </Form>
    )
}

export default FormHasImage
