import React, { useState } from 'react'
import {
    Box,
    Grid2,
    Typography,
    Container,
    Button,
    Paper,
    TextField,
    MenuItem,
    FormLabel
} from '@mui/material';
import { useDispatch, useSelector } from 'react-redux';
import { generate } from '../actions/comfy.js';

import UploadImageZone from './UploadImageZone.js';

const sizes = [
    {
        value: {
            width: 512, height: 512
        },
        label: '512 x 512'
    },
    {
        value: {
            width: 768, height: 512
        },
        label: '768 x 512'
    },
]

const steps = [
    {
        value: 25,
        label: '25'
    },
    {
        value: 30,
        label: '30'
    },
    {
        value: 35,
        label: '35'
    },
    {
        value: 40,
        label: '40'
    },
]

const Form = ({ title, hasImage = false }) => {
    const dispatch = useDispatch();
    const [formData, setFormData] = useState({
        positive: '',
        negative: '',
        steps: steps[0].value,
        size: sizes[0].value,
        image: ''
    })

    const handleGenerateClick = (e) => {
        e.preventDefault();
        dispatch(generate(formData));
    }

    return (
        <Paper elevation={6} raised>
            <Grid2 container component='form' noValidate autoComplete="off" direction='column' spacing={4} sx={{ px: 4, py: 4 }}>
                <Typography variant='h5'>{title}</Typography>
                <TextField fullWidth multiline name='positive' label='Positive Prompt'
                    value={formData.positive} onChange={(e) => {
                        setFormData({ ...formData, positive: e.target.value });
                    }} />
                <TextField fullWidth multiline name='negative' label='Negative Prompt'
                    value={formData.negative} onChange={(e) => {
                        setFormData({ ...formData, negative: e.target.value });
                    }} />
                <Grid2 container direction='row'>
                    <TextField
                        sx={{ flexGrow: 1 }}
                        select
                        label="Step"
                        helperText="Please select step"
                        value={formData.steps}
                        onChange={(e) => {
                            setFormData({ ...formData, steps: e.target.value });
                        }}
                    >
                        {steps.map((option) => (
                            <MenuItem key={option.value} value={option.value}>
                                {option.label}
                            </MenuItem>
                        ))}
                    </TextField>
                    <TextField
                        sx={{ flexGrow: 1 }}
                        select
                        label="Size Image"
                        value={formData.size}
                        helperText="Please select your size"
                        onChange={(e) => {
                            setFormData({ ...formData, size: e.target.value });
                        }}
                    >
                        {sizes.map((option) => (
                            <MenuItem key={option.value} value={option.value}>
                                {option.label}
                            </MenuItem>
                        ))}
                    </TextField>
                </Grid2>
                {hasImage ? (<UploadImageZone fileBase64={formData.image} onChange={(e) => {
                    setFormData({ ...formData, image: e });
                }} />) : ''}
                <Button
                    size='large' type='submit' variant='contained' color='primary'
                    onClick={handleGenerateClick}
                >
                    Generate Image
                </Button>
            </Grid2>
        </Paper>
    )
}

export default Form
